    __expose();
    const GetData = () => {
      const listData = ref([]);
      // 网络请求数据
      const getData = async url => {
        console.log(url);
        // 拿到结果值
        // 正在热映 /api/v1/getNowPlayingFilmList
        // 即将上映 /api/v1/getComingSoonFilmList
        const res = await axios({
          url: url
        });
        //  存储结果
        console.log(res.data.data.films);
        listData.value = res.data.data.films;
      };
      return {
        getData,
        listData
      };
    };
    const __returned__ = {
      GetData,
      get axios() {
        return axios;
      },
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
