import { getMovieList } from "@/servies/modules/movie_list";
import { ref } from "vue";

// 创建movieList变量存储下面拿到的res数据

export default {
  __name: 'movieList',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const movieList = ref([]);
    //请求数据
    getMovieList().then(res => {
      // console.log(res.data.films)
      //把请求到的数据存起来
      movieList.value = res.data.films; //这个movielist是对象，所以是存到它里面的value里面
    });

    const __returned__ = {
      movieList,
      get getMovieList() {
        return getMovieList;
      },
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
